var feedback=document.querySelector(".feedback"),popup=document.querySelector(".modal-feedback"),close=popup.querySelector(".modal-close"),form=popup.querySelector("form"),login=popup.querySelector("[name=name]"),email=popup.querySelector("[name=e-mail]"),storage=localStorage.getItem("name");feedback.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-show"),storage?(login.value=storage,email.focus()):login.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-show"),login.focus()}),form.addEventListener("submit",function(e){login.value&&email.value?localStorage.setItem("name",login.value):(e.preventDefault(),console.log("Нужно ввести имя и почту"))}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-show")&&popup.classList.remove("modal-show")});